import React, { useState, useRef } from 'react';
import { Button, Form } from 'react-bootstrap';


function theForm() {

    // const [formState, setFormState] = useState({ username: '', location: '' });
    // const [characterCount, setCharacterCount] = useState(0);
    const fileInput = useRef(null);

    const handleChange = event => {
        if (event.target.value.length <= 280) {
          setFormState({ ...formState, [event.target.name]: event.target.value });
          setCharacterCount(event.target.value.length);
        }
      };

      const handleImageUpload = event => {
        event.preventDefault();
        const data = new FormData();
        data.append('image', fileInput.current.files[0]);

        const postImage = async () => {
          try {
            const res = await fetch('/api/file-upload', {
              mode: 'cors',
              method: 'POST',
              body: data
            })
            if (!res.ok) throw new Error(res.statusText);
            const postResponse = await res.json();
            setFormState({...formState, image: postResponse.Location})
            console.log("postImage: ", postResponse.Location)
            return postResponse.Location;
          } catch (error) {
            console.log(error);
          }
        };
        postImage();
      };

                // submit form
        const handleFormSubmit = event => {
            event.preventDefault();
            // POST method with formState
            const postData = async () => {
            const res = await fetch('/api/users', {
                method: 'POST',
                headers: {
                Accept: 'application/json',
                'Content-Type': 'application/json'
                },
                body: JSON.stringify(formState)
            })
            const data = await res.json();
            console.log(data);
            }
            postData();
            // clear form value
            setFormState({ username: '', thought: '' });
            setCharacterCount(0);
        };


    return (
                <Form>
        <Form.Group >
            <Form.Label>Plant Name</Form.Label>
            <Form.Control type="text" placeholder="Eastern Redbud" />

        </Form.Group>
        <Form.Group >
            <Form.Label>Location</Form.Label>
            <Form.Control type="text" placeholder="Austin" />

        </Form.Group>
        <Form.Group >
            <Form.Label>Plant/Seed Cost</Form.Label>
            <Form.Control type="text" placeholder="$5" />

        </Form.Group>
        <Form.Group >
            <Form.Label>Description</Form.Label>
            <Form.Control type="text" placeholder="Redbud is a 15-30 ft. tree with one to several picturesque, maroon-purple trunks and a wide, umbrella-like crown. " />

        </Form.Group>
        <Form.Group>
        <Form.File id="exampleFormControlFile1" label="Plant Image" />
        </Form.Group>


        <Button variant="primary" type="submit">
            Submit
        </Button>
        </Form>


    )
}
export default theForm
